import { useState } from 'react';
import './Navbar.css';

function Navbar(){

    //The logic part of the navbar
    const [toggleMenu, setToggleMenu] = useState(false);

    const toggleNav = () => {
        setToggleMenu(!toggleMenu)
    }
    return(
        <div>
            <nav>
                {toggleMenu && (
                    <ul className="list">
                    <li className="item">Home</li>
                    <li className="item">Contact</li>
                    <li className="item">About</li>
                </ul>
                )} 
                <button className="btn" onClick={toggleNav}>btn</button>
            </nav>
            
        </div>
    )
}
export default Navbar;

/*  

    NOTE: what the {toggle menu} mean is that if toggle menu is false,
    it won't show anything but if it is true, it would show the navbar


*/
