import { useState } from "react";

function Mapping(){

    
   const array = []

   
       const func = () => {
            array.push('mine')
       }
        
    
       
      const add = () => {
        setInterval(func, 1000)
      }

    

    return(
        <div style={{marginTop:20}}>
            <h3>mapping in ul</h3>
            <ul>
                { array.map(arr => {
               return<li>{arr}</li>
               })
               }
            </ul>

            <button onClick={add}>Add to list</button> 

        </div>
    );
}


export default Mapping;
