import { useEffect, useState } from 'react';
import ChatBar from './ChatBar';
import ChatBody from './ChatBody';
import ChatFooter from './ChatFooter';


//Update the ChatPage.js file to listen to the 
//message from the server and display it to all users.

function ChatPage({ socket }){
    const [messages, setMessages] = useState([]);

    useEffect(() => {
      socket.on('messageResponse', (data) => setMessages([...messages, data]));
    }, [socket, messages]); 

    //fetches the user is typing information.
    useEffect(() => {
        socket.on('typingResponse', (data) => setTypingStatus(data));
      }, [socket]);
    
    return(
        <div className="chat">
            <ChatBar socket={socket}/>
            <div className="chat__main">
                <ChatBody messages={messages}/>
                <ChatFooter socket={socket}/>
            </div>
        </div>
    );
}

export default ChatPage;

/*IMPORTANT NOTE:

    From the code snippet above, Socket.io listens to the messages 
    sent via the messageResponse event and spreads the data into 
    the messages array. The array of messages is passed into the 
    ChatBody component for display on the UI. Update the ChatBody.js 
    file to render the data from the array of messages.

*/
