import './ChatBody.css';
import notification from './notification.mp3'//This for the incoming message notification
import { v4 as uuid } from 'uuid';
import { FaTelegramPlane, FaTrash } from 'react-icons/fa'
import { useContext, useEffect, useRef, useState } from 'react';
import io  from 'socket.io-client';
import { LoggedInNameContext } from '../contextFolder/LoggedInNameContext';
import { OtherUserNameContext } from '../contextFolder/OtherUserNameContext';
import axios from 'axios';


const socket = io.connect("http://localhost:4000"); //making a connection to the server


function ChatBody(){

    //This state is for the message that will be sent to the 
    //server so it can be emitted.
    const [text, setText] = useState('')


    //This state is what will be mapped and printed on the users UI  
    //const [lists, setLists] = useState([''])
    const [mappingTheChats, setMappingTheChats] = useState([{}])  


    //This is for displaying when the other user is typing...
    const[userIsTyping, setUserIsTyping] = useState('')


    //This is used to change the value of the input to empty string
    const inputRef = useRef()


    //This is for our scrollable chat body
    const scrollRef = useRef()
    //   scrollRef.scrollTop = scrollRef.scrollHeight;

    //logged in name ref
    const loggedInNameMessagesRef = useRef()
    
  
    //This is the scrollToBottomAlwaysOnNewMessageRef
    const scrollToBottomAlwaysOnNewMessageRef = useRef(null)


const [logColor, setLogColor] = useState('black')


    
   
    //This sound for an incoming message
   const playNotification = () => {
    const sound = new Audio(notification); 
    sound.play()
    }
   
    //Our useConetext for this file
    const { loggedInNameValue, setLoggedInNameValue } = useContext(LoggedInNameContext) 

    const { OtherUserNameValue, setOtherUserNameValue } = useContext(OtherUserNameContext)


    //fetching old chats with OtherUserNameValue
    /*
    axios.post("http://localhost:4000/fetchOldMessagesWithThisUser", {

       from: loggedInNameValue,

       to: OtherUserNameValue

    }).then((response) => {

        console.log(response)

    }).catch((err) => {

        console.log(err)
        
    })
    */

    //This is used to delete a message when the trashcan is
    //clicked on.
    const messageDelete = (id) => {
        const newMappingTheChat = mappingTheChats.filter(l => l.id !== id)
        setMappingTheChats(newMappingTheChat)
    }



    //This will be used to dictect when the other user is typing...
    //and then it will send the ('typing') message
    //to the other user connected in the socket server.
    const handleTyping = () => {
        socket.emit('typing', {typingNotification: 'typing...'})
    }


    //This will involke when the other user is not typing or they
    //just got off the input box
    const handleNotTyping = () => {
        socket.emit('notTyping', ' ')
    }


        
    //This emits the username of the logged in user to the server once login successful
    //so that it can assign a socket id to the login user
    useEffect(() => {
        
        if (loggedInNameValue) {

            socket.emit("username", { username: loggedInNameValue } );

        }

    }, [socket])
   


    
    //The sendMessage function will be used to send the message
    //data in the input box to the server so that the server can 
    //emit the message data to all the client connected to it.
    //NOTE: data must be sent to the server first before it can be
    //emitted to other client. React can't directly emit the message
    //data to other client without first getting to the server.
    const sendMessage = () => {

        //Here we want to send message to the server to receive so
        //that it can emit it to other client. After the "send_message"
        //event listener, an object should follow it which will contain
        //the message data information
        socket.emit("send_message", {message: text, from: loggedInNameValue, to: OtherUserNameValue})

        //This is used to change the value of the input to empty string
        //when the send button is clicked
       // inputRef.current.value = " ";
       // setText(' '); remove the comment from this line of code, this code is important
       
    };


    //We will be listening to the receive message event here after
    //the sever has receive a message from a user and want to emit it
    //to every user connected.
    useEffect(() => {
        socket.on("receive_message", (data)  => {  
            
        //The (data.message) below holds the message property value received from the server and
        //we give each message a random unique ID so that we can do stuff like
        //deleteing the message using it unique ID etc...
        const m = data.message;
        const i = uuid();  
        
        

        
        //This keeps whatever is in the lists state and add a new data to the list ( in this case, 
        // which is the message ) so it can get mapped   
        
        setMappingTheChats((prevState) => [...prevState, { messageData: m, id: i } ]);     
 
        if (data.message == loggedInNameValue) {
            //loggedInNameMessagesRef.current.style.color = 'white'
                 setLogColor('white')
             }
                     
       
        //This gives a notification alert when a message comes in...
       // playNotification()      
 
        })
    }, [socket])



    //This useEffect handles when the other user is typing.
    //it receive the information from the server so
    //we can display it on the front end.

    useEffect(() =>{

        socket.on("isTyping", (data) => {
          
            setUserIsTyping(data.typingNotification) 
            console.log(userIsTyping)

        })
        

    }, [socket])


     //This useEffect handles when  the other user is not typing.
     //it will emit an empty string
     useEffect(() => {
        socket.on("isNotTyping", (data) => {
            setUserIsTyping(data)
        })
     }, [socket])



     //scroll effect
     useEffect(() => {
  //This will make sure the bottom image is always shown when new message enters.
  scrollToBottomAlwaysOnNewMessageRef.current?.scrollIntoView(false)
        
    }, [mappingTheChats])









    return(
        <div style={{border:"5px solid black", paddingBottom: "800px"}}>
            <div className="chatNav">
                Your conversation with:  {OtherUserNameValue} {} {userIsTyping}
            </div>
 

            <div className="scroll" ref={scrollRef} >
                     
                {
                   
                    mappingTheChats.map((mappingTheChat, id) => (  

                     <div key={id} id="listMessageData" ref={loggedInNameMessagesRef} style={{color: logColor}}>

                            {mappingTheChat.messageData}
                                      
                        </div>
                    ))

        
                }

        <div ref={scrollToBottomAlwaysOnNewMessageRef} />
        
               
            </div>

            <div className="text-box">

                <input type="text" placeholder="message..." 

                onChange={(e) => setText(e.target.value)} //This is to store what user write inside the text variable

                className="text"

                ref={inputRef}

                onMouseEnter={handleTyping}

                onMouseLeave={handleNotTyping}
         
                />

                {/* clicking the button below sends your message*/}
                {/* <button onClick={sendMessage} className="txt-btn">send message</button> */} 
                <div>
                     <FaTelegramPlane onClick={sendMessage} className="txt-btn"/>
                </div> 
                
            </div>
             
           
        </div>
    )
}

export default ChatBody;
