//Here, weâ€™ll create the interface displaying the sent messages and the page headline.
import './ChatBody.css'
import { useNavigate } from "react-router-dom";

function ChatBody({ messages }){
    const navigate = useNavigate();

    //This code removes the user from the local storage and 
    //the page is being redirected back to the login home page
    //when the LEAVE CHAT button is clicked.
    const handleLeaveChat = () => {
        localStorage.removeItem('userName');
        navigate('/');
        window.location.reload();
    };

    return(
        <>
            <header className="chat-mainHeader">
                <p>Hangout with colleagues</p>
                <button className="leaveChat-btn" onClick={handleLeaveChat}>
                    LEAVE CHAT
                </button>
            </header>


            {/* 
                The below code snippet above displays the messages depending on 
                whether you or another user sent the message. Messages in 
                green are the ones you sent, and red is messages from other 
                users.

            */}

            <div className="message-container">

                {messages.map((message) =>
                message.name === localStorage.getItem('userName') ? (
                    <div className="message-chats-me" key={message.id}>
                    <p className="sender-name">You</p>
                    <div className="message-sender">
                        <p>{message.text}</p>
                    </div>
                    </div>
                ) : (
                    <div className="message-chats-them" key={message.id}>
                    <p>{message.name}</p>
                    <div className="message-recipient">
                        <p>{message.text}</p>
                    </div>
                    </div>
                )
                )}

                        

                {/*This is triggered when a user is typing*/}
                <div className="message-status">
                <p>typing ...</p>
                </div>
            </div>
        
        </>
    )
}

export default ChatBody;

/*
    IMPORTANT NOTE:
        The code snippet above displays the messages depending on 
        whether you or another user sent the message. Messages in 
        green are the ones you sent, and red is messages from other 
        users.

        Congratulations ðŸ¥‚, the chat application is now functional. 
        You can open multiple tabs and send messages from one to another.

