import './App.css';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Login from './asset/AccessFolder/Login';
import ChatParent from './asset/ChatParentFolder/ChatParent';
import Signup from './asset/AccessFolder/Signup';
import ChatProfile from './asset/ChatProfileFolder/ChatProfile';
import ChatList from './asset/ChatListFolder/ChatList';
import { LoggedInNameContext } from './asset/contextFolder/LoggedInNameContext';//importing LoggedInName Context
import { useState } from 'react';
import { OtherUserNameContext } from './asset/contextFolder/OtherUserNameContext';//importing OtherUserName Context
import { UserSocketId, UserSocketIdContext } from './asset/contextFolder/UserSocketIdContext';
import ChatBody from './asset/ChatBodyFolder/ChatBody';
import { OtherUserSocketIdContext } from './asset/contextFolder/OtherUserSocketIdContext';

function App(){

  //This context api useState carries the Name of the User that logged in
  const [loggedInNameValue, setLoggedInNameValue] = useState([]) 

  //This context api useState carries the Name of the other user that you can chatting with
  const [OtherUserNameValue, setOtherUserNameValue] = useState() 

  //This context api useState carries the Name of the User that logged in
  const [UserSocketIdValue, setUserSocketIdValue] = useState() 

  const [OtherUserSocketIdValue, setOtherUserSocketIdValue] = useState()

  return(
    <div>
      <LoggedInNameContext.Provider value={ { loggedInNameValue, setLoggedInNameValue } }>
        <OtherUserNameContext.Provider value={ {OtherUserNameValue, setOtherUserNameValue} }>
        <UserSocketIdContext.Provider value={ { UserSocketIdValue, setUserSocketIdValue }}>
          <OtherUserSocketIdContext.Provider value={ {OtherUserSocketIdValue, setOtherUserSocketIdValue} }>
        <BrowserRouter>
            <Routes>
              <Route path="/" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/dashboard" element={<ChatParent />} />
              <Route path="/chatprofile" element={<ChatProfile />} />
              <Route path="/chatlist" element={<ChatList />} />
              <Route path="/chatBody" element={<ChatBody />} /> 
            </Routes>
            </BrowserRouter>
            </OtherUserSocketIdContext.Provider>
            </UserSocketIdContext.Provider> 
          </OtherUserNameContext.Provider>
       </LoggedInNameContext.Provider>


</div>
  );

}

export default App;
