//Here we create the home page for the chat application
//that accepts the username and save it to the local storage
//for identification.

import './Home.css'
import {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser } from 'react-icons/fa'

function Home(){
    const navigate = useNavigate();
    const [userName, setUserName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        localStorage.setItem('userName', userName);
        navigate('/chat');
    };

    return(

        <div className="home-center">

            <form className="home-container" onSubmit={handleSubmit}>

            <div className='f'>
                <FaUser id="FaUser"/>
            </div>
            
            
                <h2 className="home-header">Sign in to open chat</h2>
                
                <div className="user-input">
                    <input
                        type="text"
                        required
                        placeholder="username"
                        minLength={6}
                        name="username"
                        id="username"
                        className="username-input"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                        /> 
                </div>

                    <div className="btn-container">
                        <button className="home-cta">SIGN IN</button>
                    </div>
            </form>
        </div>

    )
}

export default Home;
