import { useContext, useEffect, useRef, useState } from "react";
import { FaSistrix } from "react-icons/fa";
import './ChatList.css';
import axios from 'axios';
import { OtherUserNameContext } from "../contextFolder/OtherUserNameContext";
import { LoggedInNameContext } from "../contextFolder/LoggedInNameContext";



function ChatList(){

    //This is to fetch all the registered users in the database

        const [listOfUsersFromDatabase, setListOfUsersFromDatabase] = useState([ 
            {name: "kingsley"},
            {name: "john"},
            {name: "king"},
            {name: "kingslet"},
            {name: "cindy"},
        ])
   console.log(listOfUsersFromDatabase)
        useEffect(( ) => {

            axios.get("http://localhost:4000/getUser").then((response) => {
           
 //           setListOfUsersFromDatabase(response.data)
        
            } ).catch ( (error) => {
        
                console.log("oops! Couldn't reach the server" + error)
        
            } )
        }, [])
        
    

    //useRefs for this component
  //  
    const filterParentContainerRef = useRef()

    const conversedWithParentContainerRef = useRef()


    const hideFilterParentContainerRef = () => {

    //    filterParentContainerRef.current.style.display = "none"

    //    conversedWithParentContainerRef.style.display = "true"

    }


    const showFilterParentContainerRef = () => {

   //     filterParentContainerRef.current.style.display = "block"

   //     conversedWithParentContainerRef.style.display = "none"

    }

    const hideConveresedParentContainerRef = () => {

   //      conversedWithParentContainerRef.style.display = "hidden"

   //      filterParentContainerRef.current.style.display = "visible"


    }


    const [showFilterParentContainer, setShowFilterParentContainer] = useState(true)
    const [showConversedWithParentContainer, setConversedWithParentContainer] = useState(true)

    const [hideFilterParentContainer, setHideFilterParentContainer] = useState("block")
    const [hideConversedWithParentContainer, setHideConversedWithParentContainer] = useState(block)


    //Context api for this file
    const { OtherUserNameValue, setOtherUserNameValue } = useContext(OtherUserNameContext) 



    /* - - - - - [ALL USESTATE FOR THIS PARTICULAR COMPONENT] - - - - - */

    //This will help us storing the list of user that we've conversed with
    const [conversedWithLists, setConversedWithLists] = useState([''])

    //This setFilteredData will store the filtered element based on the user
    //search input and then store it in the filteredData variable so we can
    //map it
    const [filteredData, setFilteredData] = useState([])

    //This is used for capturing what the user types into the search box
    const [searchWord, setSearchWord] = useState();
   


    /* - - - - - [ALL OUR FUNCTIONS FOR THIS PARTICULAR COMPONENT] - - - - -*/

    //This is to store what the user type in the search box so we can
    //use for filtering and also filter the name of user in realtime 
    //based on serach input
    const onChangeFunction = ( event ) => {

        //This will grab the value in the user type in the input box and store it in the searchFilter state
        //please note: the toLowerCase() will convert the value to lowercase so that it can match with the
        //lowercase name properties in the database. (we'd convert all name properties in DB to lowercase too)
         setSearchWord(event.target.value.toLowerCase())
        
        //This will filter out users that matches the character inputted by the user in realtime
        //and then store it in the newFilter variable. please note: the toLowerCase() will convert the value of the
        // DB name property to lowercase so that it can match with the value from the search input which we already 
        //convertted to lowercase using the toLowercase() method.
        const newFilter = listOfUsersFromDatabase.filter( (listOfUsers) => {  

        //Here we're returning users that their names matches with the value the user is inputting as they
        //are typing it out on the go...
        return listOfUsers.name.toLowerCase().includes(searchWord.toLowerCase())
          
            
        })


/*
        if(searchWord.length > 0){

            hideConveresedParentContainerRef()
            

           }

*/
        //Meaning if the searchWord is empty, return an empty string
           if(searchWord.length < 1){

            setFilteredData([{}])
            
            

           } else {

            //This will set the searchLists to the new filtered data stored in the arr variable
            /* setFilteredData(newFilter)*/

             setFilteredData(newFilter)
        }
           

        //This sets the converesed with display to be displayed as none when search is 
        //being carried on.


        setHideConversedWithParentContainer("none")



    
    }


   
    //This function is used for getting the user with the name that matches what the user is typed in
    //the search box once they clicked the search button close to the search input box
    const searchUser = () => {

       const newArray = listOfUsersFromDatabase.filter( (listOfUsers) => {
          
        //Here, we're saying: if the name the user provide doesn't exist, we should print an error message
        //else, the program should return the name that matches what the user is searching for
        
          if (listOfUsers.name.toLowerCase() !== searchWord.toLowerCase() ){

               return console.log('user dont exist');

          } else { 
            
            return listOfUsers.name.toLowerCase() === searchWord.toLowerCase(); 
        
        }
      })

      setFilteredData(newArray)
     

    }

  
 
    return(
        <div style={{border:"5px solid black"}}>

            {/*This is for the search user's input box*/}

            <div className="search-input-div">

                <input type="text" className="search-input" onChange={onChangeFunction}></input>

                <FaSistrix className="search-input-icon" onClick={searchUser}/>
              
            </div>


            {/*This is for when user is searching for name. It filters the names available based on
               the user query or search */}
            <div className="filterAndConversewithParentDiv">


             <div className="filterParentContainer" ref={filterParentContainerRef} style={{display:'block'}}>

                {
                    filteredData.map((filter, id) => ( 

                    <div key={id} className="d"  onClick={( ) => {

                        if (conversedWithLists.includes(filter.name)) {

                        //Here we are saying if the conversedWithLists state have the name
                        //of the filteredData, it shouldn't add to it list but if it does,
                        //it should add to it list. And we handled that with the else if.  

                        } else if( !conversedWithLists.includes(filter.name) ){

                            setConversedWithLists( (prevState) => [...prevState, filter.name ]) 
                        }
                         

                     //   hideFilterParentContainerRef()
                      // conversedWithListsRef.current.style.display = 'none'  

                      //creating room for loggedInUser and this selected user
                     
                        
                        }}>
                       
                        <p>{filter.name}</p>

                    </div>
                    
                    ))
              
                 }
           
             </div>




          {/*This maps who the user has conversed with. it shows all the list*/}
        <div className="conversedWithParentContainer" ref={conversedWithParentContainerRef}>
          { 
          
            conversedWithLists.map((conversedWithList, id) => ( 

                <div key={id}  className="conversed" onClick={ ( ) => {

                   {/*onClick of a user in the chatlist will pass the name of the user to 
                    the chatbody using the contextapi crafted for OtherUserNameValue*/} 
                  
                    setOtherUserNameValue(conversedWithList)
                   

                }}>

                    <p>{conversedWithList}</p>
                   
                </div>
                
                ))
            }       
        </div>






        </div>


        </div>
    )
}

export default ChatList;
