//Here, weâ€™ll create the interface displaying the sent messages and the page headline.

import { useNavigate } from "react-router-dom";

function ChatBody(){
    const navigate = useNavigate();

    const handleLeaveChat = () => {
        localStorage.removeItem('userName');
        navigate('/');
        window.location.reload();
    };

    return(
        <>
            <header className="chat__mainHeader">
                <p>Hangout with colleagues</p>
                <button className="leaveChat__btn" onClick={handleLeaveChat}>
                    LEAVE CHAT
                </button>
            </header>

            <div className="message__container">

                {messages.map((message) =>
                message.name === localStorage.getItem('userName') ? (
                    <div className="message__chats" key={message.id}>
                    <p className="sender__name">You</p>
                    <div className="message__sender">
                        <p>{message.text}</p>
                    </div>
                    </div>
                ) : (
                    <div className="message__chats" key={message.id}>
                    <p>{message.name}</p>
                    <div className="message__recipient">
                        <p>{message.text}</p>
                    </div>
                    </div>
                )
                )}

                        

                {/*This is triggered when a user is typing*/}
                <div className="message__status">
                <p>Someone is typing...</p>
                </div>
            </div>
        
        </>
    )
}

export default ChatBody;

/*
    IMPORTANT NOTE:
        The code snippet above displays the messages depending on 
        whether you or another user sent the message. Messages in 
        green are the ones you sent, and red is messages from other 
        users.

        Congratulations ðŸ¥‚, the chat application is now functional. 
        You can open multiple tabs and send messages from one to another.

