function Mapping(){

    const array = ['mine', 'add', 'yours'];
   

   const add = () => {
        array.push('mine')
        console.log(array)
    }

    return(
        <div style={{marginTop:20}}>
            <h3>mapping in ul</h3>
            <ul>
                {array.map(arr => {
                    return<li>{arr}</li>
                })}
            </ul>

           <button onClick={add}>Add to list</button> 

        </div>
    );
}


export default Mapping;
