function Mapping(){

    const array = [ ];
   

   const add = () => {
        array.push('mine')
        console.log(array)

        const arrList = array.map(arr => {
            return<li>{arr}</li>
        })
    }

    return(
        <div style={{marginTop:20}}>
            <h3>mapping in ul</h3>
            <ul>
                {arrList}
            </ul>

           <button onClick={add}>Add to list</button> 

        </div>
    );
}


export default Mapping;
