//Here we create the home page for the chat application
//that accepts the username and save it to the local storage
//for identification.

import './Home.css'
import {useState} from 'react';
import { useNavigate } from 'react-router-dom';

function Home(){
    const navigate = useNavigate();
    const [userName, setUserName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        localStorage.setItem('userName', userName);
        navigate('/chat');
    };

    return(
        <div className="home-center">
            <form className="home-container" onSubmit={handleSubmit}>
                <h2 className="home-header">Sign in to open chat</h2>
                {userName}
                <label htmlFor="username">Username</label>
                <input
                    type="text"
                    minLength={6}
                    name="username"
                    id="username"
                    className="username-input"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    /> <br><br></br></br>
                    <button className="home-cta">SIGN IN</button>
            </form>
        </div>
    )
}

export default Home;
