import { useContext, useState } from 'react';
import './Settings.css'
import { FaUser } from 'react-icons/fa';
import axios from 'axios';
import { LoggedInNameContext } from '../contextFolder/LoggedInNameContext';

function Settings () {

      const { loggedInNameValue, setLoggedInNameValue } = useContext(LoggedInNameContext) 

      //This handles the upload of profile picture

      const [postImage, setPostImage] = useState( {myFile : ""} )


      const createPost = async (newImage) => {
        try {

            await axios.post("http://localhost:4000/uploadProfilePhoto", newImage)

        } catch (error) {
            console.log(error)
        }
      }

      const handleSendImage = async () => {
        createPost(postImage)
        console.log(postImage)
        console.log("uploaded")
      }

      const handleFileUpload = async (e) => {

        const file = e.target.files[0]
        const base64 = await convertToBase64(file)
        setPostImage({...postImage, myFile : base64, name: loggedInNameValue })
     
      }

      function convertToBase64(file){

        return new Promise((resolve, reject) => {

            const fileReader = new FileReader();

            fileReader.readAsDataURL(file)

            fileReader.onload = () => {

                resolve(fileReader.result)

            };

            fileReader.onerror = (error) => {
                reject(error)
            }
        })
      }



      
  
    return(
        <div style={{background: 'white'}} >

            <div className="settings-body">

                <h1>Settings...</h1>

                <div className="setDiv">

                    <div className="photo-change">

                        <p>profile photo: </p>

                        <div className="pic-display">
  
                        <img src={postImage.myFile} width="200px"/>

                    </div>

                        <input type="file" name="myFile" onChange={ handleFileUpload } accept="jpeg, .png, .jpg"/>   
                        <button onClick={handleSendImage}>save picture</button>

                </div>

                    <div className="password-change">
                        <p>Change password</p>
                        
                        <input type="text" className="input"></input>
                       
                    </div>

                

                </div>







          </div>

        </div>
    );
}

export default Settings;
