import { useState } from "react";
import { FaSistrix } from "react-icons/fa";
import './ChatList.css';

function ChatList(){
    //This is our data of users fetched from our database
    const [listOfUserFromDatabase, setListOfUserFromDatabase] = useState([{
        name:"Tom",
        age: 16,
        color:"purple",
        id:1
    },
    {
        name:"Kim",
        age: 17,
        color:"black",
        id:2
    },

    {
        name:"Rio",
        age: 19,
        color:"green",
        id:3
    },

    {
        name:"Joy",
        age: 17,
        color:"white",
        id:4
    },

    {
        name:"Joy",
        age: 15,
        color:"white",
        id:5
    },

    {
        name:"Rick",
        age: 17,
        color:"white",
        id:6
    }])



    /* - - - - - [ALL USESTATE FOR THIS PARTICULAR COMPONENT] - - - - - */

    //storing the list of user from DB here so we can map it on the drop down
    //search div section
    const [searchLists, setSearchLists] = useState( listOfUserFromDatabase )


    //This is used for capturing what the user types into the search box
    const [searchFilter, setSearchFilter] = useState();



    /* - - - - - [ALL OUR FUNCTIONS FOR THIS PARTICULAR COMPONENT] - - - - -*/

    //This is to store what the user type in the search box so we can
    //use for filtering and also filter the name of user in realtime 
    //based on serach input
    const onChangeFunction = ( event ) => {

        //This will grab the value in the user type in the input box and store it in the searchFilter state
        //please note: the toLowerCase() will convert the value to lowercase so that it can match with the
        //lowercase name properties in the database. (we'd convert all name properties in DB to lowercase too)
         setSearchFilter(event.target.value.toLowerCase())
        
        //This will filter out users that matches the character inputted by the user in realtime
        //and the store it in the arr variable. please note: the toLowerCase() will convert the value of the
        //DB name property so that it can match with the value from the search input which we already convertted
        //to lowercase using the toLowercase() method.
        const arr = searchLists.filter( (s) => {
            if(searchFilter == " "){
                return searchLists
            } else if ( s.name.toLowerCase().includes(searchFilter.toLowerCase()) ) {
                return s
            }
            
            
        })
   
        //This will set the searchLists to the new filtered data stored in the arr variable
        setSearchLists(arr)   
        

    }


    //This function is used for searching for user with the name that 
    //matches what the user is searching
    const searchUser = () => {

       const newArray = searchLists.filter( (searchList) => {
          

    /*      if (searchList.name.toLowerCase() !== searchFilter ){

                console.log('user dont exist');

          } else { */
            
            return searchList.name.toLowerCase() === searchFilter; 
        
  //      }
      })

      setSearchLists(newArray)
     

    }


    return(
        <div style={{border:"5px solid black", paddingBottom: "800px"}}>

            <div className="search-input-div">

                <input type="text" className="search-input" onChange={onChangeFunction}></input>

                <FaSistrix className="search-input-icon" onClick={searchUser}/>
                <select>
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                </select>

            </div>
            
            {
                searchLists.map((sL, id) => ( 
                <div key={id} style={{marginTop:20, marginBottom:20}}>
                    <p>{sL.name}</p>
                    <p>{sL.age}</p>
                    <p>{sL.color}</p>
                    <p>{sL.id}</p>
                </div>
                
                ))
            
            } 

        </div>
    )
}

export default ChatList;
