import { useState } from "react";

function Mapping(){

    const array = [ ];
   
   const [list, setList] = useState()
   const add = () => {
        array.push('mine')
        console.log(array)

        setList( array.map(arr => {
            return<li>{arr}</li>
        }))
    }

    return(
        <div style={{marginTop:20}}>
            <h3>mapping in ul</h3>
            <ul>
                {list}
            </ul>

           <button onClick={add}>Add to list</button> 

        </div>
    );
}


export default Mapping;
