import { useEffect, useRef, useState } from 'react';
import socketIoClient from 'socket.io-client'

const NEW_CHAT_MESSAGE_EVENT = 'newChatMessage'; //Name of the event
const SOCKET_SERVER_URL = 'http://localhost:5000';

function useChat(roomId){
    const [messages, setMessages] = useState([]); //Sent and received messages
    const socketRef = useRef();

    useEffect(() => {
        //create a websocket connection
        socketRef.current = socketIOClient(SOCKET_SERVER_URL, {
            query: { roomId },
        });

        //Listens for incoming messages
        socketRef.current.on(NEW_CHAT_MESSAGE_EVENT, (message) => {
            const incomingMessage = {
                ...message, 
                ownedByCurrentUser: message.senderId === socketRef.current.id,

            };
            setMessages((messages) => [...message, incomingMessage]);
        });

        //Destroy the socket reference when the connection is closed
        return () => {
            socketRef.current.disconnect();
        };

    }, [roomId]);

    //Send a message to the server that forwards it to all users in the same room
    const sendMessage = (messageBody) => {
        socketRef.current.emit(NEW_CHAT_MESSAGE_EVENT, {
            body: messageBody,
            senderId: socketRef.current.id,
        });
    };


    return { messages, sendMessage };




}



export default useChat;
