import { useContext, useEffect, useState } from 'react';
import './UserProfile.css'
import { FaUserAlt } from "react-icons/fa";
import axios from 'axios';
import { LoggedInNameContext } from '../contextFolder/LoggedInNameContext';

function UserProfile () {

    const {loggedInNameValue, setLoggedInNameValue} = useContext(LoggedInNameContext)

    useEffect(() => {
        axios.post("http://localhost:4000/getUserProfile", {
            
        name: loggedInNameValue
    
    }).then((response) => {

            console.log(response.data)
            setName(response.data.name)
            setEmail(response.data.email)

        }).catch((err) => {console.log(err)})

    }, [])

    const [name, setName] = useState() 
    const [email, setEmail] = useState()
    const [phoneNumber, setPhoneNumber] = useState()

    return(
        <div style={{ background: 'white' }}>  
        <div className="userProfile-body">

            <div className="top-flex">
                <div className="profile-photo">
                     <FaUserAlt className="photo"/>
                </div>
            
                <div className="name">{name}</div>
            </div>


            <div className="grid">

                <div>
                    <label className="label">Name</label>
                    <p className='p'>{name}</p>
                </div>
               
                <div>
                    <label className="label">Email</label>
                    <p className='p'>{email}</p>
                </div>

                <div>
                    <label className="label">Phone number</label>
                    <p className='p'>sss</p>
                </div>

                <div>
                    <label className="label">Password</label>
                    <p className='p'>sss</p>
                </div>

            </div>

            <div className="btn-div">
                    <button className="btn"> save changes </button> 
            </div>
            
        </div>

    </div>   
    )
}

export default UserProfile;
