//Here we create the home page for the chat application
//that accepts the username and save it to the local storage
//for identification.

import './Login.css'
import {useState} from 'react';
//mport { useHistory } from 'react-router-dom';
import { FaUser } from 'react-icons/fa'

function Login(){
 //   const navigate = useNavigate();
    const [userName, setUserName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
       // navigate('/chat');

       axios.post("http://localhost:4000/login", {}).then((response) => { }).catch((error) => { console.log(error)})
    };

    return(

        <div className="login-center">

            <form className="login-container" onSubmit={handleSubmit}>

            <div className='f'>

                <FaUser id="FaUser"/>
            </div>
            
            
                <h2 className="login-header">Sign in to open chat</h2>
                
                <div className="user-input">
                    <input
                        type="text"
                        required
                        placeholder="username"
                        minLength={6}
                        name="username"
                        id="username"
                        className="username-input"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                        /> 

                    <input
                        type="password"
                        required
                        placeholder="password"
                        minLength={6}
                        name="password"
                        id="password"
                        className="password-input"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                        /> 
                </div>

                    <div className="btn-container">
                        <button className="login-cta">SIGN IN</button>
                    </div>

                    <p className="login-sign-up">Dont have an account? <a href="">Sign up</a></p>
                    <p className="forgot-password">
                        <a href="#" >forgot password</a>
                    </p>
            </form>
        </div>

    )
}

export default Login;
