import './App.css';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Login from './asset/AccessFolder/Login';
import ChatParent from './asset/ChatParentFolder/ChatParent';
import Signup from './asset/AccessFolder/Signup';
import ChatProfile from './asset/ChatProfileFolder/ChatProfile';
import ChatList from './asset/ChatListFolder/ChatList';
import { LoggedInNameContext } from './asset/contextFolder/LoggedInNameContext';
import { useState } from 'react';

function App(){

  //This context api is 
  const [loggedInNameValue, setLoggedInNameValue] = useState([]) 

  return(
    <div>
      <LoggedInNameContext.Provider value={ { loggedInNameValue, setLoggedInNameValue } }>
        <BrowserRouter>
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/dashboard" element={<ChatParent />} />
              <Route path="/chatprofile" element={<ChatProfile />} />
              <Route path="/chatlist" element={<ChatList />} />
            </Routes>
          </BrowserRouter>
       </LoggedInNameContext.Provider>


</div>
  );

}

export default App;
