import { useState } from 'react';
import './Settings.css'
import { FaUser } from 'react-icons/fa';
import axios from 'axios';

function Settings () {
 //   const [file, setFile] = useState();
    const [postImage, setPostImage] = useState({ myFile : "" })

    const handleFileChange = async (e) => {
        const file = e.target.files[0]
        const base64 = await convertToBase64(file)
        console.log(base64)

        setPostImage({...postImage, myFile : base64})

    }

   function convertToBase64 (file) {

    return new Promise ((resolve, reject) => {

        const fileReader = new FileReader();

        fileReader.readAsDataURL(file);

        fileReader.onload = () => {

            resolve(fileReader.result)

        };

        fileReader.onerror = (error) => {

            reject(error)
        }

    })
       
    }

    const createPost = async (newImage) => {

        try {

            await axios.post("http://localhost:4000/uploadProfilePhoto", newImage)

        } catch (error) {

            console.log(error)
            
        }
    }

    const uploadProfilePicture = () => {
        createPost(postImage)
        console.log(postImage)
        console.log("uploaded")
    }
    return(
        <div style={{background: 'white'}} >

            <div className="settings-body">

                <h1>Settings...</h1>

                <div className="setDiv">

                    <div className="photo-change">

                        <p>profile photo: </p>

                        <div className="pic-display">

                        {/*
                            file ? <img src={file} width="200px" heigth="200px"/> : 
                            
                            <FaUser className="settings-FaUser"/>
    */ }
                        
                    </div>

                        <input type="file" onChange={handleFileChange}/>   
                        <button onClick={uploadProfilePicture}>save picture</button>

                </div>

                    <div className="password-change">
                        <p>Change password</p>
                        <input type="text" className="input"></input>
                    </div>

                

                </div>







          </div>

        </div>
    );
}

export default Settings;
