import './Settings.css'
import { FaUser } from 'react-icons/fa';
import axios from 'axios';

function Settings () {
    const [localStorageName, setLocalStorageName] = useState(loggedInNameValue)
    useEffect(() => {

        let storedData = localStorage.getItem("username")

            if (storedData) {

                setLocalStorageName(JSON.parse(storedData))

            }  
     
    }, []);




 //   const [file, setFile] = useState();
 
      const uploadProfilePhoto = async () => {
        await axios.post("http://localhost:4000/uploadProfilePhoto", {})
      }
           
      
  
    return(
        <div style={{background: 'white'}} >

            <div className="settings-body">

                <h1>Settings...</h1>

                <div className="setDiv">

                    <div className="photo-change">

                        <p>profile photo: </p>

                        <div className="pic-display">

                        {/*
