import { useState } from "react";
import { FaSistrix } from "react-icons/fa";
import './ChatList.css';

function ChatList(){

    const [people, setPeople] = useState([{
        name:"Tom",
        age: 16,
        color:"purple",
        id:1
    },
    {
        name:"Kim",
        age: 17,
        color:"black",
        id:2
    },

    {
        name:"Rio",
        age: 19,
        color:"green",
        id:3
    },

    {
        name:"Joy",
        age: 17,
        color:"white",
        id:4
    }])

    const [filt, setFilt] = useState();
    const [searchlist, setSearchlist] = useState([{}])

    const searchUser = () => {

       const newArray = people.filter( (p) => {
            p.name.includes(setFilt)
      })

     setPeople(newArray)

    }
    return(
        <div style={{border:"5px solid black", paddingBottom: "800px"}}>

            <div className="search-input-div">

                <input type="text" className="search-input" onChange={(e) => setFilt(e.target.value)}></input>

                <FaSistrix className="search-input-icon" onClick={searchUser}/>

            </div>
            
            
            {filt}
            {
                people.map((sL, id) => ( 
                <div key={id} style={{marginTop:20, marginBottom:20}}>
                    <p>{sL.name}</p>
                    <p>{sL.age}</p>
                    <p>{sL.color}</p>
                    <p>{sL.id}</p>
                </div>
                
                ))
            
            } 

        </div>
    )
}

export default ChatList;
