import { useState } from "react";
import { FaSistrix } from "react-icons/fa";
import './ChatList.css';

function ChatList(){

    const [people, setPeople] = useState([{
        name:"Tom",
        age: 16,
        color:"purple",
        id:1
    },
    {
        name:"Kim",
        age: 17,
        color:"black",
        id:2
    }])

    const [filt, setFilt] = useState();
    const [sl, setSl] = useState()

    const searchUser = () => {
       const newArray = people.filter( ( ) => {
            people .includes(filt)
       })
    }
    return(
        <div style={{border:"5px solid black", paddingBottom: "800px"}}>

            <div className="search-input-div">

                <input type="text" className="search-input" onChange={(e) => setFilt(e.target.value)}></input>

                <FaSistrix className="search-input-icon" onClick={searchUser}/>

            </div>
            
            
            {sl}

            {
                people.map((pip, id) => ( 
                <div key={id} style={{marginTop:20, marginBottom:20}}>
                    <p>{pip.name}</p>
                </div>
                
                ))
            
            } 

        </div>
    )
}

export default ChatList;
