//Here we create the home page for the chat application
//that accepts the username and save it to the local storage
//for identification.

import './Login.css';
import axios from 'axios'
import {useState} from 'react';
//mport { useHistory } from 'react-router-dom';
import { FaUser } from 'react-icons/fa'

function Login(){
 //   const navigate = useNavigate();
    const [userName, setUserName] = useState('');
    const [userPassword, setUserPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
       // navigate('/chat');

       axios.post("http://localhost:4000/login", {

        name: userName,

        password: userPassword

       }).then((response) => {

          console.log(response.data) 

          //If we get a positive result from the response of the fetch and not an error,
          //we should get the user_id of the user loggin in so that we can 
          //use that to retreive all the unique information about that specific 
          //user throughout our application. The user_id will be stored in the new object
          //called {result} that we created in our server controller for handling login.
          if(response.data.result){

            const loginUserId = response.data.result;

          //if the user is accessing this application on a larger screen size, we are then
          //going to route from this page to the dashboard once login is successful.
          
            

          } else if(response.data.error){
            //If there was an error with getting due to wrong user login input, we'd get 
            //notified here...
            console.log('bad')
          }
        

       }).catch((error) => {
             //This error is for the promise. If there was an error 
             console.log(error) 
       });


    };

    return(

        <div className="login-center">

            <form className="login-container" onSubmit={handleSubmit}>

            <div className='f'>

                <FaUser id="loginFaUser"/>

            </div>
            
            
                <h2 className="login-header">Sign in to open chat</h2>
                
                <div className="user-input">
                    <input
                        type="text"
                        required
                        placeholder="username"
                        minLength={6}
                        name="username"
                        id="username"
                        className="username-input"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                        /> 

                    <input
                        type="password"
                        required
                        placeholder="password"
                        minLength={6}
                        name="password"
                        id="password"
                        className="password-input"
                        value={userPassword}
                        onChange={(e) => setUserPassword(e.target.value)}
                        /> 
                </div>

                    <div className="btn-container">
                        <button className="login-cta">SIGN IN</button>
                    </div>

                    <p className="login-sign-up">Dont have an account? <a href="">Sign up</a></p>
                    <p className="forgot-password">
                        <a href="#" >forgot password</a>
                    </p>

            </form>
            
        </div>

    )
}

export default Login;
