import {FaHamburger } from 'react-icons/fa';
//import { } from 'react-icons/fa';

import {FaFacebookSquare } from 'react-icons/fa';
import { FaTimesCircle } from 'react-icons/fa';
import './Navbar.css';
import { useRef } from 'react';


function Navbar(){

   const inputRef = useRef();

    const openMenu = () => {
     
        inputRef.current.style.display = 'flex';
        inputRef.current.style.top = '0';

    }

    const closeMenu = () => {
        
    }


    return(
        <div>
            <nav>
                <div className="flex1">
                     <h1>Nav</h1>
                </div>

                <div className="openMenu"  onClick={openMenu}  ref={inputRef}>
                    <FaHamburger  />
                </div>
                
                <div className="mainMenu">
                    <ul className="list">
                        <li className="item">Home</li>
                        <li className="item">Contact</li>
                        <li className="item">About</li>

                        <div className="closeMenu"><FaTimesCircle /></div>
                        
                        <span className="icons">
                            <FaFacebookSquare />

                        </span>
                    </ul>
                </div>
            </nav>
            
        </div>
    )
}
export default Navbar;

/*  

    NOTE: what the {toggle menu} mean is that if toggle menu is false,
    it won't show anything but if it is true, it would show the navbar


*/
