import { useState } from "react";
import { FaSistrix } from "react-icons/fa";
import './ChatList.css';

function ChatList(){

    const [listOfUserFromDatabase, setListOfUserFromDatabase] = useState([{
        name:"Tom",
        age: 16,
        color:"purple",
        id:1
    },
    {
        name:"Kim",
        age: 17,
        color:"black",
        id:2
    },

    {
        name:"Rio",
        age: 19,
        color:"green",
        id:3
    },

    {
        name:"Joy",
        age: 17,
        color:"white",
        id:4
    }])

    const [searchList, setSearchList] = useState( listOfUserFromDatabase )

    const [searchFilter, setSearchFilter] = useState();

   console.log(searchList)
    const searchUser = () => {

       const newArray = searchList.filter( (search) => {
          

          if (search.name.toLowerCase() !== searchFilter ){

                console.log('user dont exist');

          } else { 
            
            return search.name.toLowerCase() === searchFilter; 
        
        }
      })

      setSearchList(newArray)
     

    }
    return(
        <div style={{border:"5px solid black", paddingBottom: "800px"}}>

            <div className="search-input-div">

                    <input type="text" className="search-input" onChange={
                        
                        (e) => setSearchFilter(e.target.value.toLowerCase())
                    
                        //setSearchList()
                    }></input>

                <FaSistrix className="search-input-icon" onClick={searchUser}/>

            </div>
            
            
            {
                searchList.map((sL, id) => ( 
                <div key={id} style={{marginTop:20, marginBottom:20}}>
                    <p>{sL.name}</p>
                    <p>{sL.age}</p>
                    <p>{sL.color}</p>
                    <p>{sL.id}</p>
                </div>
                
                ))
            
            } 

        </div>
    )
}

export default ChatList;
