//Here, we’ll create the input and the send button at the 
//bottom of the chat page. The message and the username 
//appear in the console after submitting the form.

import { useState } from 'react';

function ChatFooter({ socket }) {
    const [message, setMessage] = useState('');

    const handleTyping = () => {
      socket.emit('typing', `${localStorage.getItem('userName')} is typing...`);
    }
  

    // The handleSendMessage function checks if the text field is empty 
    //and if the username exists in the local storage (sign-in from the Home page) 
    // before sending the message event containing 
    //the user input, username, the message ID generated, 
    // and the socket or client ID to the Node.js server.
    
    const handleSendMessage = (e) => {
      e.preventDefault();
      if (message.trim() && localStorage.getItem('userName')) {
        socket.emit('message', {
          text: message,
          name: localStorage.getItem('userName'),
          id: `${socket.id}${Math.random()}`,
          socketID: socket.id,
        });
      }

      setMessage('');
    };
    return (
      <div className="chat__footer">
        <form className="form" onSubmit={handleSendMessage}>
          <input
            type="text"
            placeholder="Write message"
            className="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleTyping}//This sense when a user is typing
          />
          <button className="sendBtn">SEND</button>
        </form>
      </div>
    );
  };
  
  export default ChatFooter;



  /* IMPORTANT NOTE:

        The handleSendMessage function checks if the text field is empty 
        and if the username exists in the local storage (sign-in from the Home page) 
        before sending the message event containing 
        the user input, username, the message ID generated, 
        and the socket or client ID to the Node.js server.

  */
