import { useState } from 'react';
import './Settings.css'
import { FaUser } from 'react-icons/fa';
import axios from 'axios';

function Settings () {


    

 //   const [file, setFile] = useState();
 
      const uploadProfilePhoto = async () => {
        await axios.post("http://localhost:4000/uploadProfilePhoto", {})
      }
           

      const handleSubmit = (e) => {
        e.preventDefault()
      }

      const handleFileUpload = async (e) => {

        const file = e.target.files
        console.log(file)
      }

      function convertToBase64(file){
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file)
            fileReader.onload = () => {
                resolve(fileReader.result)
            };
            fileReader.onerror = (error) => {
                reject(error)
            }
        })
      }



      
  
    return(
        <div style={{background: 'white'}} >

            <div className="settings-body">

                <h1>Settings...</h1>

                <div className="setDiv">

                    <div className="photo-change">

                        <p>profile photo: </p>

                        <div className="pic-display">

                        {/*
                            file ? <img src={file} width="200px" heigth="200px"/> : 
                            
                            <FaUser className="settings-FaUser"/>
    */ }
                    {/* <img src={} width="200px"/>  */} 
                    </div>

                        <input type="file" name="myFile" onChange={(e) => handleFileUpload} accept="jpeg, .png, .jpg"/>   
                        <button onClick={uploadProfilePhoto}>save picture</button>

                </div>

                    <div className="password-change">
                        <p>Change password</p>
                        <input type="text" className="input"></input>
                    </div>

                

                </div>







          </div>

        </div>
    );
}

export default Settings;
