import { useState } from 'react';
import './Settings.css'
import { FaUser } from 'react-icons/fa';
import axios from 'axios';
function Settings () {
    const [file, setFile] = useState();

    const handleFileChange = (e) => {

        setFile(URL.createObjectURL(e.target.files[0]));

    }

    const saveChanges = async () => {
        const formData = new FormData ()
        formData.append("image", file)
   //     formData.append("description", description)

        const result = await axios.post("http://localhost:4000/uploadProfilePhoto", formData, { 
            headers: {'Content-Type': 'multipart/form-data'}
        
        })

        console.log(result.data)
    }

    return(
        <div style={{background: 'white'}} >

            <div className="settings-body">

                <h1>Settings...</h1>

                <div className="setDiv">

                    <div className="photo-change">

                        <p>profile photo: </p>

                        <div className="pic-display">

                        {
                            file ? <img src={file} width="200px" heigth="200px"/> : 
                            
                            <FaUser className="settings-FaUser"/>
                        }
                        
                    </div>

                        <input type="file" onChange={handleFileChange}/>   
                        <button onClick={uploadProfilePi}>save changes</button>

                </div>

                    <div className="password-change">
                        <p>Change password</p>
                        <input type="text" className="input"></input>
                    </div>

                

                </div>







          </div>

        </div>
    );
}

export default Settings;
