import './ChatProfile.css'
import { FaCommentDots, FaGlobe, FaRegSun, FaUser, FaUserAlt } from 'react-icons/fa';
import { useContext, useEffect, useState } from 'react';
import { LoggedInNameContext } from '../contextFolder/LoggedInNameContext';
import { ProfileDisplayContext } from '../contextFolder/ProfileDisplayContext';
import { MessageDisplayContext } from '../contextFolder/MessageDisplayContext';
import { SettingsDisplayContext } from '../contextFolder/SettingsDisplayContext';
import axios from 'axios';


function ChatProfile(){

    //This context api useState carries the Name of the User that logged in
    const { loggedInNameValue, setLoggedInNameValue } = useContext(LoggedInNameContext) 


    const { profileDisplayValue, setProfileDisplayValue } = useContext(ProfileDisplayContext)
    const { messageDisplayValue, setMessageDisplayValue } = useContext(MessageDisplayContext)
    const {settingsDisplayValue, setSettingsDisplayValue } = useContext(SettingsDisplayContext)

    const clickProfile = () => {
        setMessageDisplayValue(false)
        setSettingsDisplayValue(false)
        setProfileDisplayValue(true)

    }

    const clickMessage = () => {
        setProfileDisplayValue(false)
        setSettingsDisplayValue(false)
        setMessageDisplayValue(true)
    }

    const clickSettings = () => {
        setProfileDisplayValue(false)
        setMessageDisplayValue(false)
        setSettingsDisplayValue(true)

    }

/*
   //This is to save the username of the logged in user to local storage
    const [localStorageName, setLocalStorageName] = useState(loggedInNameValue)

    useEffect(() => {

        let storedData = localStorage.getItem("username")

            if (storedData) {

                setLocalStorageName(JSON.parse(storedData))

            }  
     
    }, []);


    useEffect(() => {

        localStorage.setItem("username", JSON.stringify(localStorageName))
       
    }, [localStorageName]);

*/




    //Fetching user's profile photo
    const [fetchProfilePhoto, setFetchProfilePhoto] = useState()
    useEffect(() => {
        axios.post("http://localhost:4000/getUserProfile", {
            name: loggedInNameValue
        }).then((response) => {

            console.log(response)

        }).catch((error) => {

            console.log(error)
        })

    }, [])







    
    return(
        <div style={{border:"5px solid black", paddingBottom: "800px"}}>
           

            <div className="profile-top">
                <FaUserAlt className="icon"/>

                <p>
                  {/* localStorageName ? localStorageName : 'username?' */}
                  {loggedInNameValue ? loggedInNameValue : 'mm'}
                 
                </p>
                 

            </div>
          
            <div className="prof">
                <div className="profile-list"><FaGlobe className="FaGlobe"/> Activity</div>
                <div className="profile-list" onClick={clickProfile}><FaUser className="FaUser"/>Profile</div>
                <div className="profile-list" onClick={clickMessage}><FaCommentDots className="FaCommentDots"/> Message</div>
                <div className="profile-list" onClick={clickSettings}><FaRegSun className="FaRegSun"/> Setting</div>
            </div>
        </div>
    )
}

export default ChatProfile;
